mapper.update:
UPDATE sns.post_comments
SET status = #{status}
WHERE p_comment_id = #{commentId}

mapper.select:
SELECT c.p_comment_id,c.pid,c.post_id,c.uid,u.nickname,c.content, CASE WHEN c.status = 0 THEN '正常' WHEN c.status = 1 THEN '审核' WHEN c.status = 2 THEN '删除' END AS status, c.create_time FROM sns.post_comments c LEFT JOIN sns.sys_user u ON c.uid = u.user_id
AND c.post_id = #{postId}
AND c.uid = #{uid}
AND c.nickname LIKE CONCAT('%', #{nickname}, '%')
AND c.content LIKE CONCAT('%', #{content}, '%')
AND c.status = #{status}
ORDER BY
${sortBy} ${order}
c.p_comment_id DESC
